{/* Cast Access Modal */}
      {showCastModal && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.5)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          zIndex: 1000
        }}>
          <div style={{
            backgroundColor: 'white',
            padding: '30px',
            borderRadius: '10px',
            width: '400px',
            boxShadow: '0 4px 20px rgba(0,0,0,0.15)'
          }}>
            <h2 style={{
              margin: '0 0 20px 0',
              color: '#0066b2',
              fontSize: '22px'
            }}>Cast Member Portal</h2>
            
            {!selectedCastMember ? (
              <div>
                <p style={{ fontSize: '15px', color: '#637385', marginBottom: '15px' }}>
                  Please select your Cast Member ID:
                </p>
                <select
                  onChange={(e) => {
                    const selected = staffList.find(s => s.id === e.target.value);
