import React, { useState, useEffect } from 'react';

function SoarOps() {
  // State variables
  const [refreshCount, setRefreshCount] = useState(0);
  const [showAdminModal, setShowAdminModal] = useState(false);
  const [adminCode, setAdminCode] = useState('');
  const [isAdminMode, setIsAdminMode] = useState(false);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}));
  const [currentDate, setCurrentDate] = useState(new Date().toLocaleDateString('en-US', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  }));

  // Staff management state
  const [showStaffPanel, setShowStaffPanel] = useState(false);
  const [newStaffName, setNewStaffName] = useState('');
  const [newStaffPosition, setNewStaffPosition] = useState('Standby Greeter');
  const [newStaffTheater, setNewStaffTheater] = useState('A');
  const [newStaffId, setNewStaffId] = useState('');

  // Guest count state
  const [guestCount, setGuestCount] = useState(36542);
  const [dailyCapacity, setDailyCapacity] = useState(50000);
  const [capacityPercentage, setCapacityPercentage] = useState(73);
  const [projectedCount, setProjectedCount] = useState(42000);

  // Wait time states
  const [liveWaitTime, setLiveWaitTime] = useState(45);
  const [hourlyWaitTimes, setHourlyWaitTimes] = useState([
    { hour: '8 AM', waitTime: 30 },
    { hour: '9 AM', waitTime: 35 },
    { hour: '10 AM', waitTime: 50 },
    { hour: '11 AM', waitTime: 65 },
    { hour: '12 PM', waitTime: 70 },
    { hour: '1 PM', waitTime: 55 },
    { hour: '2 PM', waitTime: 45 },
    { hour: '3 PM', waitTime: 40 },
    { hour: '4 PM', waitTime: 35 },
    { hour: '5 PM', waitTime: 40 },
    { hour: '6 PM', waitTime: 30 },
    { hour: '7 PM', waitTime: 25 }
  ]);

  // Hourly efficiency data
  const [hourlyEfficiency, setHourlyEfficiency] = useState([
    { hour: '8 AM', efficiency: 91 },
    { hour: '9 AM', efficiency: 93 },
    { hour: '10 AM', efficiency: 96 },
    { hour: '11 AM', efficiency: 94 },
    { hour: '12 PM', efficiency: 89 },
    { hour: '1 PM', efficiency: 87 },
    { hour: '2 PM', efficiency: 90 },
    { hour: '3 PM', efficiency: 94 },
    { hour: '4 PM', efficiency: 96 },
    { hour: '5 PM', efficiency: 97 },
    { hour: '6 PM', efficiency: 95 },
    { hour: '7 PM', efficiency: 92 }
  ]);

  // Theater status management
  const [theaterStatus, setTheaterStatus] = useState({
    'A': 'Operational',
    'B': 'Operational',
    'C': 'Maintenance'
  });

  // Theater names
  const theaterNames = {
    'A': 'A Theater',
    'B': 'B Theater',
    'C': 'C Theater'
  };

  // Position options
  const positions = [
    'Standby Greeter',
    'Lightning Lane 1',
    'Lightning Lane 2',
    'Tour Desk',
    'Lost & Found',
    'Pre-Merge',
    'Merge',
    'Tower A',
    'Tower B',
    'Tower C',
    'Grouper',
    'A Gate',
    'B Gate',
    'C Gate',
    'Assist',
    'Wheelchair Assist'
  ];

  // Staff list
  const [staffList, setStaffList] = useState([
    { id: 'CM-10045', name: 'John Smith', position: 'Tower A', theater: 'A', efficiency: 95, status: 'On Duty' },
    { id: 'CM-10102', name: 'Sarah Johnson', position: 'Lightning Lane 1', theater: 'A', efficiency: 92, status: 'On Duty' },
    { id: 'CM-10234', name: 'Michael Brown', position: 'Standby Greeter', theater: 'A', efficiency: 88, status: 'On Duty' },
    { id: 'CM-10311', name: 'Emily Davis', position: 'Pre-Merge', theater: 'B', efficiency: 94, status: 'On Duty' },
    { id: 'CM-09876', name: 'David Wilson', position: 'Tower B', theater: 'B', efficiency: 90, status: 'On Duty' },
    { id: 'CM-10567', name: 'Jessica Taylor', position: 'Lightning Lane 2', theater: 'B', efficiency: 89, status: 'On Duty' },
    { id: 'CM-10789', name: 'Robert Martinez', position: 'Tower C', theater: 'C', efficiency: 91, status: 'On Duty' },
    { id: 'CM-10456', name: 'Lisa Anderson', position: 'Merge', theater: 'C', efficiency: 87, status: 'On Break' }
  ]);

  // Calculate average wait time
  const averageWaitTime = Math.round(
    hourlyWaitTimes.reduce((sum, item) => sum + item.waitTime, 0) / hourlyWaitTimes.length
  );

  // Calculate average efficiency
  const averageEfficiency = Math.round(
    hourlyEfficiency.reduce((sum, item) => sum + item.efficiency, 0) / hourlyEfficiency.length
  );

  // Calculate active staff by theater
  const getActiveStaffCount = (theater) => {
    return staffList.filter(s => s.theater === theater && s.status === 'On Duty').length;
  };

  // Timer for updating current time and capacity percentage
  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date();
      setCurrentTime(now.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}));
      
      // Update capacity with slight random fluctuation
      const randomChange = Math.floor(Math.random() * 3) - 1; // -1, 0, or 1
      setCapacityPercentage(prevPercentage => {
        const newPercentage = Math.max(60, Math.min(95, prevPercentage + randomChange));
        return newPercentage;
      });
      
      // Update efficiency with minor fluctuations
      setHourlyEfficiency(prev => {
        const currentHour = now.getHours();
        let hourString = currentHour >= 12
          ? `${currentHour > 12 ? currentHour - 12 : currentHour} PM`
          : `${currentHour === 0 ? 12 : currentHour} AM`;
          
        return prev.map(item => {
          if (item.hour === hourString) {
            const newEfficiency = Math.max(85, Math.min(98, item.efficiency + (Math.random() > 0.5 ? 1 : -1)));
            return { ...item, efficiency: newEfficiency };
          }
          return item;
        });
      });
    }, 60000); // Update every minute
    
    return () => clearInterval(timer);
  }, []);

  // Update capacity whenever percentage changes
  useEffect(() => {
    setGuestCount(Math.round(dailyCapacity * capacityPercentage / 100));
  }, [capacityPercentage, dailyCapacity]);

  // Update hourly wait times when live wait time changes
  const updateHourlyWaitTimes = () => {
    const currentHour = new Date().getHours();
    let hourString = currentHour >= 12
      ? `${currentHour > 12 ? currentHour - 12 : currentHour} PM`
      : `${currentHour === 0 ? 12 : currentHour} AM`;

    setHourlyWaitTimes(hourlyWaitTimes.map(item =>
      item.hour === hourString
        ? { ...item, waitTime: liveWaitTime }
        : item
    ));
  };

  // Admin verification
  const verifyAdminCode = () => {
    if (adminCode === '123456789') {
      setIsAdminMode(true);
      setShowAdminModal(false);
    } else {
      alert('Invalid authorization code. Please try again or contact your supervisor.');
    }
  };

  // Staff management
  const handlePositionChange = (id, newPosition) => {
    setStaffList(staffList.map(staff =>
      staff.id === id ? { ...staff, position: newPosition } : staff
    ));
  };

  const handleTheaterChange = (id, newTheater) => {
    setStaffList(staffList.map(staff =>
      staff.id === id ? { ...staff, theater: newTheater } : staff
    ));
  };

  const handleStatusChange = (id) => {
    setStaffList(staffList.map(staff =>
      staff.id === id ? { ...staff, status: staff.status === 'On Duty' ? 'On Break' : 'On Duty' } : staff
    ));
  };

  const handleAddStaff = () => {
    if (!newStaffName) {
      alert("Please enter cast member's name");
      return;
    }

    if (!newStaffId) {
      alert("Please enter cast member's ID");
      return;
    }

    const newStaff = {
      id: newStaffId,
      name: newStaffName,
      position: newStaffPosition,
      theater: newStaffTheater,
      efficiency: 85,
      status: 'On Duty'
    };

    setStaffList([...staffList, newStaff]);
    setNewStaffName('');
    setNewStaffId('');
    alert('Cast member added successfully!');
  };

  // Theater status management
  const handleTheaterStatusChange = (theater, status) => {
    setTheaterStatus({
      ...theaterStatus,
      [theater]: status
    });
  };

  // Guest count management
  const handleGuestCountChange = (amount) => {
    setGuestCount(Math.max(0, guestCount + amount));
    // Update capacity percentage
    setCapacityPercentage(Math.round((guestCount + amount) / dailyCapacity * 100));
  };

  const handleCustomGuestCountChange = (newCount) => {
    const count = parseInt(newCount);
    if (!isNaN(count) && count >= 0) {
      setGuestCount(count);
      // Update capacity percentage
      setCapacityPercentage(Math.round(count / dailyCapacity * 100));
    }
  };

  // Wait time management
  const handleLiveWaitTimeChange = (amount) => {
    setLiveWaitTime(Math.max(0, liveWaitTime + amount));
    setTimeout(updateHourlyWaitTimes, 10);
  };

  const handleCustomWaitTimeChange = (newTime) => {
    const time = parseInt(newTime);
    if (!isNaN(time) && time >= 0) {
      setLiveWaitTime(time);
      setTimeout(updateHourlyWaitTimes, 10);
    }
  };

  // Color functions for status
  const getStatusColor = (status) => {
    switch (status) {
      case 'Operational': return '#10b981'; // green
      case 'Maintenance': return '#f59e0b'; // yellow/orange
      case 'Down': return '#ef4444'; // red
      default: return '#10b981';
    }
  };

  // Get staff status color
  const getStaffStatusColor = (status) => {
    return status === 'On Duty' ? '#10b981' : '#f59e0b';
  };

  // Get wait time color based on average
  const getWaitTimeColor = (waitTime) => {
    if (waitTime <= 30) return '#10b981'; // green for short wait
    if (waitTime <= 60) return '#f59e0b'; // yellow/orange for medium wait
    return '#ef4444'; // red for long wait
  };

  // Format capacity as percentage
  const formatCapacity = () => {
    return `${capacityPercentage}%`;
  };

  // Format numbers with commas
  const formatNumber = (num) => {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  // Button and table cell styles
  const buttonStyle = {
    padding: '8px 14px',
    backgroundColor: '#f1f5fb',
    color: '#0066b2',
    border: '1px solid #e0e8f5',
    borderRadius: '5px',
    cursor: 'pointer',
    fontWeight: '500',
    fontSize: '14px',
    transition: 'all 0.2s ease'
  };
  
  const primaryButtonStyle = {
    padding: '8px 16px',
    backgroundColor: '#0066b2',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontWeight: '500',
    fontSize: '14px',
    transition: 'all 0.2s ease'
  };
  
  const statusThStyle = {
    padding: '12px 15px',
    border: '1px solid #e0e8f5',
    backgroundColor: '#f1f5fb',
    fontWeight: '600',
    color: '#0066b2',
    textAlign: 'left'
  };
  
  const statusTdStyle = {
    padding: '12px 15px',
    border: '1px solid #e0e8f5',
    textAlign: 'left'
  };

  return (
    <div style={{
      padding: '30px',
      backgroundColor: '#f5f9ff',
      fontFamily: '"Avenir Next", "Segoe UI", Arial, sans-serif',
      borderRadius: '12px',
      boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
      maxWidth: '1200px',
      margin: '30px auto',
      border: '1px solid #e0e8f5'
    }}>
      {/* Admin Login Modal */}
      {showAdminModal && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.5)',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          zIndex: 1000
        }}>
          <div style={{
            backgroundColor: 'white',
            padding: '30px',
            borderRadius: '10px',
            width: '400px',
            boxShadow: '0 4px 20px rgba(0,0,0,0.15)'
          }}>
            <h2 style={{
              margin: '0 0 20px 0',
              color: '#0066b2',
              fontSize: '22px'
            }}>Leadership Authentication</h2>
            <p style={{ fontSize: '15px', color: '#637385', marginBottom: '20px', lineHeight: '1.5' }}>
              Please enter your authorization code to access operational management tools.
            </p>
            <input
              type="password"
              value={adminCode}
              onChange={(e) => setAdminCode(e.target.value)}
              placeholder="Enter authorization code"
              style={{
                width: '100%',
                padding: '14px',
                borderRadius: '6px',
                border: '1px solid #ddd',
                marginBottom: '24px',
                fontSize: '16px'
              }}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  verifyAdminCode();
                }
              }}
            />
            <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '15px' }}>
              <button
                style={{
                  padding: '12px 20px',
                  backgroundColor: '#f5f5f5',
                  color: '#333',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '15px'
                }}
                onClick={() => setShowAdminModal(false)}
              >
                Cancel
              </button>
              <button
                style={{
                  padding: '12px 20px',
                  backgroundColor: '#0066b2',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '15px',
                  fontWeight: '500'
                }}
                onClick={verifyAdminCode}
              >
                Authenticate
              </button>
            </div>
          </div>
        </div>
      )}

      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: '20px',
        borderBottom: '2px solid #e0e8f5',
        paddingBottom: '20px'
      }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <div style={{
            width: '45px',
            height: '45px',
            backgroundColor: '#0066b2',
            borderRadius: '8px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: 'white',
            fontWeight: 'bold',
            fontSize: '22px',
            marginRight: '16px',
            boxShadow: '0 3px 6px rgba(0, 102, 178, 0.2)'
          }}>SO</div>
          <div>
            <h1 style={{
              color: '#0066b2',
              fontSize: '28px',
              margin: '0 0 6px 0',
              fontWeight: '600',
              letterSpacing: '-0.5px'
            }}>SoarOps Command Center</h1>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              color: '#637385',
              fontSize: '15px',
              gap: '20px'
            }}>
              <p style={{ margin: 0 }}>Attraction Operations Management</p>
              <span style={{ color: '#0066b2', fontWeight: '500' }}>{currentTime}</span>
              <span style={{ color: '#637385' }}>{currentDate}</span>
            </div>
          </div>
        </div>
        {/* Admin Button */}
        <button
          style={{
            padding: '12px 24px',
            backgroundColor: isAdminMode ? '#10b981' : '#0066b2',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
            fontSize: '16px',
            fontWeight: '500',
            boxShadow: '0 3px 10px rgba(0, 0, 0, 0.2)',
            transition: 'all 0.2s ease'
          }}
          onClick={() => isAdminMode ? setIsAdminMode(false) : setShowAdminModal(true)}
        >
          {isAdminMode ? 'Exit Leadership Mode' : 'Leadership Access'}
        </button>
      </div>

      {/* Admin Panel */}
      {isAdminMode && (
        <div style={{ marginBottom: '30px' }}>
          {/* Admin Control Bar */}
          <div style={{
            backgroundColor: 'white',
            padding: '18px',
            borderRadius: '10px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
            border: '1px solid #eef2f8',
            marginBottom: '25px',
            display: 'flex',
            gap: '15px'
          }}>
            <button
              style={{
                padding: '12px 20px',
                backgroundColor: showStaffPanel ? '#dcfce7' : '#10b981',
                color: showStaffPanel ? '#10b981' : 'white',
                border: showStaffPanel ? '1px solid #10b981' : 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontWeight: '500',
                fontSize: '15px',
                transition: 'all 0.2s ease'
              }}
              onClick={() => setShowStaffPanel(!showStaffPanel)}
            >
              {showStaffPanel ? 'Close Cast Management' : 'Cast Management'}
            </button>
          </div>

          {/* Staff Management Panel */}
          {showStaffPanel && (
            <div style={{
              backgroundColor: 'white',
              padding: '28px',
              borderRadius: '10px',
              boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
              border: '1px solid #eef2f8',
              marginBottom: '25px'
            }}>
              <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 20px 0', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span>Cast Member Management</span>
              </h3>
              {/* Add Staff Form */}
              <div style={{
                backgroundColor: '#f8fbff',
                padding: '24px',
                borderRadius: '8px',
                border: '1px solid #eef2f8',
                marginBottom: '25px'
              }}>
                <h4 style={{
                  margin: '0 0 15px 0',
                  color: '#0066b2',
                  fontSize: '17px',
                  fontWeight: '600'
                }}>Add New Cast Member</h4>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '18px', marginBottom: '22px' }}>
                  <div>
                    <label style={{
                      display: 'block',
                      marginBottom: '8px',
                      fontSize: '14px',
                      color: '#637385',
                      fontWeight: '500'
                    }}>
                      Full Name:
                    </label>
                    <input
                      type="text"
                      value={newStaffName}
                      onChange={(e) => setNewStaffName(e.target.value)}
                      placeholder="Enter cast member name"
                      style={{
                        width: '100%',
                        padding: '12px',
                        borderRadius: '6px',
                        border: '1px solid #ddd',
                        fontSize: '15px'
                      }}
                    />
                  </div>
                  <div>
                    <label style={{
                      display: 'block',
                      marginBottom: '8px',
                      fontSize: '14px',
                      color: '#637385',
                      fontWeight: '500'
                    }}>
                      Cast Member ID:
                    </label>
                    <input
                      type="text"
                      value={newStaffId}
                      onChange={(e) => setNewStaffId(e.target.value)}
                      placeholder="e.g. CM-12345"
                      style={{
                        width: '100%',
                        padding: '12px',
                        borderRadius: '6px',
                        border: '1px solid #ddd',
                        fontSize: '15px'
                      }}
                    />
                  </div>
                  <div>
                    <label style={{
                      display: 'block',
                      marginBottom: '8px',
                      fontSize: '14px',
                      color: '#637385',
                      fontWeight: '500'
                    }}>
                      Position:
                    </label>
                    <select
                      value={newStaffPosition}
                      onChange={(e) => setNewStaffPosition(e.target.value)}
                      style={{
                        width: '100%',
                        padding: '12px',
                        borderRadius: '6px',
                        border: '1px solid #ddd',
                        fontSize: '15px'
                      }}
                    >
                      {positions.map((position, index) => (
                        <option key={index} value={position}>{position}</option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label style={{
                      display: 'block',
                      marginBottom: '8px',
                      fontSize: '14px',
                      color: '#637385',
                      fontWeight: '500'
                    }}>
                      Theater Assignment:
                    </label>
                    <select
                      value={newStaffTheater}
                      onChange={(e) => setNewStaffTheater(e.target.value)}
                      style={{
                        width: '100%',
                        padding: '12px',
                        borderRadius: '6px',
                        border: '1px solid #ddd',
                        fontSize: '15px'
                      }}
                    >
                      <option value="A">{theaterNames['A']}</option>
                      <option value="B">{theaterNames['B']}</option>
                      <option value="C">{theaterNames['C']}</option>
                    </select>
                  </div>
                </div>
                <button
                  style={{
                    padding: '12px 22px',
                    backgroundColor: '#10b981',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontWeight: '500',
                    fontSize: '15px',
                    transition: 'all 0.2s ease'
                  }}
                  onClick={handleAddStaff}
                >
                  Add Cast Member
                </button>
              </div>
              {/* Staff Table */}
              <div style={{ overflowX: 'auto' }}>
                <table style={{
                  width: '100%',
                  borderCollapse: 'collapse',
                  backgroundColor: '#fff',
                  borderRadius: '6px',
                  overflow: 'hidden',
                  boxShadow: '0 1px 3px rgba(0,0,0,0.05)'
                }}>
                  <thead>
                    <tr style={{ backgroundColor: '#f1f5fb' }}>
                      <th style={{ padding: '14px', border: '1px solid #e0e8f5', textAlign: 'left' }}>Cast Member ID</th>
                      <th style={{ padding: '14px', border: '1px solid #e0e8f5', textAlign: 'left' }}>Name</th>
                      <th style={{ padding: '14px', border: '1px solid #e0e8f5', textAlign: 'left' }}>Position</th>
                      <th style={{ padding: '14px', border: '1px solid #e0e8f5', textAlign: 'left' }}>Venue</th>
                      <th style={{ padding: '14px', border: '1px solid #e0e8f5', textAlign: 'center' }}>Status</th>
                      <th style={{ padding: '14px', border: '1px solid #e0e8f5', textAlign: 'center' }}>Efficiency</th>
                    </tr>
                  </thead>
                  <tbody>
                    {staffList.map(staff => (
                      <tr key={staff.id}>
                        <td style={{ padding: '14px', border: '1px solid #e0e8f5' }}>{staff.id}</td>
                        <td style={{ padding: '14px', border: '1px solid #e0e8f5' }}>{staff.name}</td>
                        <td style={{ padding: '14px', border: '1px solid #e0e8f5' }}>
                          <select
                            value={staff.position}
                            onChange={e => handlePositionChange(staff.id, e.target.value)}
                            style={{
                              padding: '8px',
                              borderRadius: '4px',
                              border: '1px solid #ddd',
                              fontSize: '14px',
                              width: '100%'
                            }}
                          >
                            {positions.map((position, idx) => (
                              <option key={idx} value={position}>{position}</option>
                            ))}
                          </select>
                        </td>
                        <td style={{ padding: '14px', border: '1px solid #e0e8f5' }}>
                          <select
                            value={staff.theater}
                            onChange={e => handleTheaterChange(staff.id, e.target.value)}
                            style={{
                              padding: '8px',
                              borderRadius: '4px',
                              border: '1px solid #ddd',
                              fontSize: '14px',
                              width: '100%'
                            }}
                          >
                            <option value="A">{theaterNames['A']}</option>
                            <option value="B">{theaterNames['B']}</option>
                            <option value="C">{theaterNames['C']}</option>
                          </select>
                        </td>
                        <td style={{ padding: '14px', border: '1px solid #e0e8f5', textAlign: 'center' }}>
                          <button
                            onClick={() => handleStatusChange(staff.id)}
                            style={{
                              padding: '6px 12px',
                              backgroundColor: getStaffStatusColor(staff.status),
                              color: 'white',
                              border: 'none',
                              borderRadius: '4px',
                              cursor: 'pointer',
                              fontSize: '14px',
                              width: '90px'
                            }}
                          >
                            {staff.status}
                          </button>
                        </td>
                        <td style={{ 
                          padding: '14px', 
                          border: '1px solid #e0e8f5', 
                          textAlign: 'center',
                          fontWeight: 'bold',
                          color: staff.efficiency >= 90 ? '#10b981' : '#f59e0b'
                        }}>
                          {staff.efficiency}%
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Main Dashboard */}
      <div style={{
        display: 'flex',
        gap: '25px',
        flexWrap: 'wrap'
      }}>
        {/* Left Column */}
        <div style={{ flex: 1, minWidth: '340px' }}>
          {/* Guest Count */}
          <div style={{
            backgroundColor: 'white',
            borderRadius: '10px',
            padding: '24px',
            marginBottom: '20px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
            border: '1px solid #eef2f8'
          }}>
            <h3 style={{ 
              color: '#0066b2', 
              fontSize: '18px', 
              margin: '0 0 15px 0', 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center' 
            }}>
              <span>Current Guest Count</span>
              <span style={{ 
                fontSize: '14px', 
                backgroundColor: capacityPercentage > 90 ? '#fee2e2' : '#dcfce7',
                color: capacityPercentage > 90 ? '#ef4444' : '#10b981',
                padding: '5px 10px',
                borderRadius: '20px',
                fontWeight: '500'
              }}>
                {formatCapacity()} Capacity
              </span>
            </h3>
            <div style={{ 
              fontSize: '34px', 
              fontWeight: 'bold', 
              color: capacityPercentage > 90 ? '#ef4444' : '#10b981', 
              marginBottom: '15px',
              display: 'flex',
              alignItems: 'baseline',
              gap: '15px'
            }}>
              {formatNumber(guestCount)}
              <span style={{ fontSize: '16px', color: '#637385', fontWeight: 'normal' }}>
                Projected: {formatNumber(projectedCount)}
              </span>
            </div>
            <div style={{ display: 'flex', gap: '10px', marginBottom: '15px', flexWrap: 'wrap' }}>
              <button onClick={() => handleGuestCountChange(100)} style={buttonStyle}>+100</button>
              <button onClick={() => handleGuestCountChange(-100)} style={buttonStyle}>-100</button>
              <button onClick={() => handleGuestCountChange(1000)} style={buttonStyle}>+1,000</button>
              <button onClick={() => handleGuestCountChange(-1000)} style={buttonStyle}>-1,000</button>
            </div>
            <div style={{ position: 'relative' }}>
              <input
                type="number"
                min="0"
                value={guestCount}
                onChange={e => handleCustomGuestCountChange(e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px 12px',
                  borderRadius: '6px',
                  border: '1px solid #ddd',
                  fontSize: '15px'
                }}
              />
              <div style={{ 
                position: 'absolute', 
                right: '12px', 
                top: '50%', 
                transform: 'translateY(-50%)',
                color: '#637385',
                fontSize: '14px'
              }}>
                guests
              </div>
            </div>
          </div>

          {/* Theater Status */}
          <div style={{
            backgroundColor: 'white',
            borderRadius: '10px',
            padding: '24px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
            border: '1px solid #eef2f8',
            marginBottom: '20px'
          }}>
            <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 15px 0' }}>Theater Status</h3>
            <div style={{ marginBottom: '20px' }}>
              {Object.entries(theaterStatus).map(([theater, status]) => (
                <div key={theater} style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between', 
                  alignItems: 'center',
                  padding: '14px 0',
                  borderBottom: '1px solid #e0e8f5'
                }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
                    <div style={{ 
                      width: '30px', 
                      height: '30px', 
                      borderRadius: '50%', 
                      backgroundColor: getStatusColor(status),
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      color: 'white',
                      fontWeight: 'bold',
                      fontSize: '14px'
                    }}>{theater}</div>
                    <div>
                      <div style={{ fontWeight: '500' }}>{theaterNames[theater]}</div>
                      <div style={{ fontSize: '13px', color: '#637385', marginTop: '2px' }}>
                        {getActiveStaffCount(theater)} active cast members
                      </div>
                    </div>
                  </div>
                  <div style={{ 
                    fontWeight: '500', 
                    color: getStatusColor(status),
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px'
                  }}>
                    <span>{status}</span>
                  </div>
                </div>
              ))}
            </div>
            {isAdminMode && (
              <div>
                <h4 style={{ fontSize: '15px', margin: '20px 0 12px 0', color: '#637385' }}>Update Theater Status:</h4>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '12px' }}>
                  {Object.keys(theaterStatus).map(theater => (
                    <div key={theater} style={{ textAlign: 'center' }}>
                      <p style={{ 
                        margin: '0 0 8px 0', 
                        fontWeight: '500', 
                        color: '#333' 
                      }}>{theaterNames[theater]}</p>
                      <select
                        value={theaterStatus[theater]}
                        onChange={e => handleTheaterStatusChange(theater, e.target.value)}
                        style={{
                          padding: '8px 10px',
                          borderRadius: '6px',
                          border: '1px solid #ddd',
                          fontSize: '14px',
                          width: '100%'
                        }}
                      >
                        <option value="Operational">Operational</option>
                        <option value="Maintenance">Maintenance</option>
                        <option value="Down">Down</option>
                      </select>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Operations Summary */}
          <div style={{
            backgroundColor: 'white',
            borderRadius: '10px',
            padding: '24px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
            border: '1px solid #eef2f8'
          }}>
            <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 15px 0' }}>Operations Summary</h3>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
              <div style={{ 
                padding: '15px', 
                borderRadius: '8px', 
                backgroundColor: '#f0f9ff',
                border: '1px solid #e0f2fe'
              }}>
                <h4 style={{ margin: '0 0 5px 0', color: '#0066b2', fontSize: '14px' }}>Cast Efficiency</h4>
                <p style={{ 
                  margin: 0, 
                  fontSize: '22px', 
                  fontWeight: 'bold', 
                  color: averageEfficiency >= 90 ? '#10b981' : '#f59e0b'
                }}>{averageEfficiency}%</p>
              </div>
              <div style={{ 
                padding: '15px', 
                borderRadius: '8px', 
                backgroundColor: '#fff7ed',
                border: '1px solid #ffedd5'
              }}>
                <h4 style={{ margin: '0 0 5px 0', color: '#f59e0b', fontSize: '14px' }}>Active Cast</h4>
                <p style={{ 
                  margin: 0, 
                  fontSize: '22px', 
                  fontWeight: 'bold', 
                  color: '#f59e0b' 
                }}>{staffList.filter(s => s.status === 'On Duty').length}</p>
              </div>
              <div style={{ 
                padding: '15px', 
                borderRadius: '8px', 
                backgroundColor: '#f0fdf4',
                border: '1px solid #dcfce7'
              }}>
                <h4 style={{ margin: '0 0 5px 0', color: '#10b981', fontSize: '14px' }}>Avg Wait Time</h4>
                <p style={{ 
                  margin: 0, 
                  fontSize: '22px', 
                  fontWeight: 'bold', 
                  color: getWaitTimeColor(averageWaitTime)
                }}>{averageWaitTime} min</p>
              </div>
              <div style={{ 
                padding: '15px', 
                borderRadius: '8px', 
                backgroundColor: '#eff6ff',
                border: '1px solid #dbeafe'
              }}>
                <h4 style={{ margin: '0 0 5px 0', color: '#3b82f6', fontSize: '14px' }}>Operational Venues</h4>
                <p style={{ 
                  margin: 0, 
                  fontSize: '22px', 
                  fontWeight: 'bold', 
                  color: '#3b82f6' 
                }}>
                  {Object.values(theaterStatus).filter(status => status === 'Operational').length} / {Object.keys(theaterStatus).length}
                </p>
              </div>
            </div>
            
            <div style={{ marginTop: '20px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <div style={{ color: '#637385', fontSize: '14px' }}>
                Last updated: {currentTime}
              </div>
              <button
                style={{
                  padding: '10px 16px',
                  backgroundColor: '#0066b2',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontWeight: '500',
                  fontSize: '14px',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}
                onClick={() => {
                  setRefreshCount(refreshCount + 1);
                  setCurrentTime(new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}));
                  const randomChange = Math.floor(Math.random() * 3) - 1;
                  setCapacityPercentage(prev => Math.max(60, Math.min(95, prev + randomChange)));
                }}
              >
                <span style={{ display: 'inline-block', width: '16px', height: '16px' }}></span>
                Refresh Data
              </button>
            </div>
          </div>
        </div>

        {/* Right Column */}
        <div style={{ flex: 2, minWidth: '400px' }}>
          {/* Live Wait Time */}
          <div style={{
            backgroundColor: 'white',
            borderRadius: '10px',
            padding: '24px',
            marginBottom: '20px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
            border: '1px solid #eef2f8'
          }}>
            <h3 style={{ 
              color: '#0066b2', 
              fontSize: '18px', 
              margin: '0 0 10px 0', 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center' 
            }}>
              <span>Live Wait Time</span>
              <span style={{ 
                fontSize: '14px', 
                backgroundColor: liveWaitTime > averageWaitTime ? '#fee2e2' : '#dcfce7',
                color: liveWaitTime > averageWaitTime ? '#ef4444' : '#10b981',
                padding: '5px 10px',
                borderRadius: '20px',
                fontWeight: '500'
              }}>
                {liveWaitTime > averageWaitTime ? 'Above Average' : 'Below Average'}
              </span>
            </h3>
            <div style={{ 
              fontSize: '38px', 
              fontWeight: 'bold', 
              color: getWaitTimeColor(liveWaitTime), 
              marginBottom: '20px',
              display: 'flex',
              alignItems: 'baseline',
              gap: '15px'
            }}>
              {liveWaitTime} min
              <span style={{ fontSize: '16px', color: '#637385', fontWeight: '500' }}>
                Avg: {averageWaitTime} min
              </span>
            </div>
            <div style={{ display: 'flex', gap: '10px', marginBottom: '15px', flexWrap: 'wrap' }}>
              <button onClick={() => handleLiveWaitTimeChange(1)} style={buttonStyle}>+1</button>
              <button onClick={() => handleLiveWaitTimeChange(-1)} style={buttonStyle}>-1</button>
              <button onClick={() => handleLiveWaitTimeChange(5)} style={buttonStyle}>+5</button>
              <button onClick={() => handleLiveWaitTimeChange(-5)} style={buttonStyle}>-5</button>
              <button onClick={() => handleLiveWaitTimeChange(15)} style={buttonStyle}>+15</button>
              <button onClick={() => handleLiveWaitTimeChange(-15)} style={buttonStyle}>-15</button>
            </div>
            <div style={{ position: 'relative' }}>
              <input
                type="number"
                min="0"
                value={liveWaitTime}
                onChange={e => handleCustomWaitTimeChange(e.target.value)}
                style={{
                  width: '100%',
                  padding: '10px 12px',
                  borderRadius: '6px',
                  border: '1px solid #ddd',
                  fontSize: '15px'
                }}
              />
              <div style={{ 
                position: 'absolute', 
                right: '12px', 
                top: '50%', 
                transform: 'translateY(-50%)',
                color: '#637385',
                fontSize: '14px'
              }}>
                minutes
              </div>
            </div>
          </div>

          {/* Hourly Stats */}
          <div style={{
            display: 'grid',
            gridTemplateColumns: '1fr 1fr',
            gap: '20px',
            marginBottom: '20px'
          }}>
            {/* Wait Time History */}
            <div style={{
              backgroundColor: 'white',
              borderRadius: '10px',
              padding: '24px',
              boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
              border: '1px solid #eef2f8'
            }}>
              <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 15px 0' }}>Hourly Wait Times</h3>
              <div style={{ maxHeight: '320px', overflowY: 'auto', borderRadius: '6px', border: '1px solid #eef2f8' }}>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead style={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                    <tr>
                      <th style={{
                        padding: '12px',
                        borderBottom: '2px solid #e0e8f5',
                        backgroundColor: '#f8fafc',
                        fontWeight: '600',
                        color: '#0066b2',
                        textAlign: 'left'
                      }}>Hour</th>
                      <th style={{
                        padding: '12px',
                        borderBottom: '2px solid #e0e8f5',
                        backgroundColor: '#f8fafc',
                        fontWeight: '600',
                        color: '#0066b2',
                        textAlign: 'right'
                      }}>Wait Time</th>
                    </tr>
                  </thead>
                  <tbody>
                    {hourlyWaitTimes.map((item, idx) => (
                      <tr key={idx} style={{ backgroundColor: idx % 2 === 0 ? '#fff' : '#f8fafc' }}>
                        <td style={{
                          padding: '12px',
                          borderBottom: '1px solid #e0e8f5',
                          fontWeight: '500'
                        }}>{item.hour}</td>
                        <td style={{
                          padding: '12px',
                          borderBottom: '1px solid #e0e8f5',
                          textAlign: 'right',
                          color: getWaitTimeColor(item.waitTime),
                          fontWeight: '600'
                        }}>{item.waitTime} min</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            {/* Efficiency History */}
            <div style={{
              backgroundColor: 'white',
              borderRadius: '10px',
              padding: '24px',
              boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
              border: '1px solid #eef2f8'
            }}>
              <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 15px 0' }}>Hourly Efficiency</h3>
              <div style={{ maxHeight: '320px', overflowY: 'auto', borderRadius: '6px', border: '1px solid #eef2f8' }}>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead style={{ position: 'sticky', top: 0, backgroundColor: 'white', zIndex: 1 }}>
                    <tr>
                      <th style={{
                        padding: '12px',
                        borderBottom: '2px solid #e0e8f5',
                        backgroundColor: '#f8fafc',
                        fontWeight: '600',
                        color: '#0066b2',
                        textAlign: 'left'
                      }}>Hour</th>
                      <th style={{
                        padding: '12px',
                        borderBottom: '2px solid #e0e8f5',
                        backgroundColor: '#f8fafc',
                        fontWeight: '600',
                        color: '#0066b2',
                        textAlign: 'right'
                      }}>Efficiency</th>
                    </tr>
                  </thead>
                  <tbody>
                    {hourlyEfficiency.map((item, idx) => (
                      <tr key={idx} style={{ backgroundColor: idx % 2 === 0 ? '#fff' : '#f8fafc' }}>
                        <td style={{
                          padding: '12px',
                          borderBottom: '1px solid #e0e8f5',
                          fontWeight: '500'
                        }}>{item.hour}</td>
                        <td style={{
                          padding: '12px',
                          borderBottom: '1px solid #e0e8f5',
                          textAlign: 'right',
                          color: item.efficiency >= 90 ? '#10b981' : item.efficiency >= 80 ? '#3b82f6' : '#f59e0b',
                          fontWeight: '600'
                        }}>{item.efficiency}%</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>

          {/* Theater Cast Stats */}
          <div style={{
            backgroundColor: 'white',
            borderRadius: '10px',
            padding: '24px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
            border: '1px solid #eef2f8'
          }}>
            <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 15px 0' }}>Theater Cast Distribution</h3>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '16px' }}>
              {Object.keys(theaterNames).map(theater => {
                const activeCount = staffList.filter(s => s.theater === theater && s.status === 'On Duty').length;
                const totalCount = staffList.filter(s => s.theater === theater).length;
                const avgEfficiency = Math.round(
                  staffList
                    .filter(s => s.theater === theater)
                    .reduce((sum, staff) => sum + staff.efficiency, 0) / 
                    (totalCount || 1)
                );
                
                return (
                  <div 
                    key={theater}
                    style={{
                      backgroundColor: '#f8fafc',
                      borderRadius: '8px',
                      padding: '16px',
                      border: '1px solid #e0e8f5'
                    }}
                  >
                    <div style={{ 
                      display: 'flex',
                      alignItems: 'center',
                      gap: '12px',
                      marginBottom: '12px'
                    }}>
                      <div style={{ 
                        width: '28px', 
                        height: '28px', 
                        borderRadius: '50%', 
                        backgroundColor: getStatusColor(theaterStatus[theater]),
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        color: 'white',
                        fontWeight: 'bold',
                        fontSize: '14px'
                      }}>{theater}</div>
                      <div style={{ fontWeight: '600', color: '#0066b2' }}>
                        {theaterNames[theater]}
                      </div>
                    </div>
                    
                    <div style={{ 
                      display: 'flex',
                      justifyContent: 'space-between',
                      borderTop: '1px solid #e0e8f5',
                      paddingTop: '12px'
                    }}>
                      <div>
                        <div style={{ fontSize: '13px', color: '#637385' }}>Active Cast</div>
                        <div style={{ 
                          fontSize: '18px', 
                          fontWeight: '600', 
                          color: '#0066b2',
                          marginTop: '4px'
                        }}>
                          {activeCount} / {totalCount}
                        </div>
                      </div>
                      
                      <div style={{ textAlign: 'right' }}>
                        <div style={{ fontSize: '13px', color: '#637385' }}>Avg Efficiency</div>
                        <div style={{ 
                          fontSize: '18px', 
                          fontWeight: '600', 
                          color: avgEfficiency >= 90 ? '#10b981' : '#f59e0b',
                          marginTop: '4px'
                        }}>
                          {avgEfficiency}%
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SoarOps;
