import React, { useState, useEffect } from 'react';

// Helper for random assignment
function getRandom(arr) {
  return arr[Math.floor(Math.random() * arr.length)];
}

function SoarOps() {
  // --- State variables ---
  const [showAdminModal, setShowAdminModal] = useState(false);
  const [adminCode, setAdminCode] = useState('');
  const [isAdminMode, setIsAdminMode] = useState(false);
  const [showCastAccess, setShowCastAccess] = useState(false);

  // Cast Access State
  const [castStatus, setCastStatus] = useState("Off Duty");
  const [castAssignment, setCastAssignment] = useState(null);

  // Dashboard/Admin State
  const [guestCount, setGuestCount] = useState(36542);
  const [liveWaitTime, setLiveWaitTime] = useState(45);
  const [hourlyWaitTimes, setHourlyWaitTimes] = useState([
    { hour: '8 AM', waitTime: 30 },
    { hour: '9 AM', waitTime: 35 },
    { hour: '10 AM', waitTime: 50 },
    { hour: '11 AM', waitTime: 65 },
    { hour: '12 PM', waitTime: 70 },
    { hour: '1 PM', waitTime: 55 },
    { hour: '2 PM', waitTime: 45 },
    { hour: '3 PM', waitTime: 40 },
    { hour: '4 PM', waitTime: 35 },
    { hour: '5 PM', waitTime: 40 },
    { hour: '6 PM', waitTime: 30 },
    { hour: '7 PM', waitTime: 25 }
  ]);
  const [hourlyEfficiency, setHourlyEfficiency] = useState([
    { hour: '8 AM', efficiency: 91 },
    { hour: '9 AM', efficiency: 93 },
    { hour: '10 AM', efficiency: 96 },
    { hour: '11 AM', efficiency: 94 },
    { hour: '12 PM', efficiency: 89 },
    { hour: '1 PM', efficiency: 87 },
    { hour: '2 PM', efficiency: 90 },
    { hour: '3 PM', efficiency: 94 },
    { hour: '4 PM', efficiency: 96 },
    { hour: '5 PM', efficiency: 97 },
    { hour: '6 PM', efficiency: 95 },
    { hour: '7 PM', efficiency: 92 }
  ]);
  const [theaterStatus, setTheaterStatus] = useState({
    'A': 'Operational',
    'B': 'Operational',
    'C': 'Maintenance'
  });

  const [showStaffPanel, setShowStaffPanel] = useState(false);
  const [newStaffName, setNewStaffName] = useState('');
  const [newStaffPosition, setNewStaffPosition] = useState('Operator');
  const [newStaffTheater, setNewStaffTheater] = useState('A');
  const [staffList, setStaffList] = useState([
    { id: 1, name: 'John Smith', position: 'Lead', theater: 'A', efficiency: 95 },
    { id: 2, name: 'Sarah Johnson', position: 'Operator', theater: 'A', efficiency: 92 },
    { id: 3, name: 'Michael Brown', position: 'Greeter', theater: 'A', efficiency: 88 },
    { id: 4, name: 'Emily Davis', position: 'Operator', theater: 'B', efficiency: 94 },
    { id: 5, name: 'David Wilson', position: 'Lead', theater: 'B', efficiency: 90 },
    { id: 6, name: 'Jessica Taylor', position: 'Greeter', theater: 'B', efficiency: 89 },
    { id: 7, name: 'Robert Martinez', position: 'Lead', theater: 'C', efficiency: 91 },
    { id: 8, name: 'Lisa Anderson', position: 'Operator', theater: 'C', efficiency: 87 }
  ]);

  // For assignment
  const positions = [
    "Standby Greeter",
    "Lightning Lane 1",
    "Lightning Lane 2",
    "Tour Desk",
    "Lost & Found",
    "Pre-Merge",
    "Merge",
    "Tower A",
    "Tower B",
    "Tower C",
    "Grouper",
    "A Gate",
    "B Gate",
    "C Gate",
    "Assist",
    "Wheelchair Assist",
    "Lead",
    "Operator",
    "Greeter",
    "Technician",
    "Supervisor"
  ];
  const theaters = ["A", "B", "C"];
  const theaterNames = { A: "A Theater", B: "B Theater", C: "C Theater" };

  // --- Leadership/Admin Modal Logic ---
  const verifyAdminCode = () => {
    if (adminCode === '123456789') {
      setIsAdminMode(true);
      setShowAdminModal(false);
    } else {
      alert('Invalid authorization code. Please try again or contact your supervisor.');
    }
  };

  // --- Cast Access Logic ---
  const handleClockIn = () => {
    setCastStatus("On Duty");
    setCastAssignment(null);
  };
  const handleGetAssignment = () => {
    setCastAssignment({
      position: getRandom(positions),
      theater: getRandom(theaters)
    });
    setCastStatus("On Duty");
  };
  const handleLunch = () => {
    setCastStatus("On Break");
  };
  const handleClockOut = () => {
    setCastStatus("Off Duty");
    setCastAssignment(null);
  };

  // --- Admin Features ---
  const handlePositionChange = (id, newPosition) => {
    setStaffList(staffList.map(staff =>
      staff.id === id ? { ...staff, position: newPosition } : staff
    ));
  };

  const handleTheaterChange = (id, newTheater) => {
    setStaffList(staffList.map(staff =>
      staff.id === id ? { ...staff, theater: newTheater } : staff
    ));
  };

  const handleAddStaff = () => {
    if (!newStaffName) {
      alert("Please enter a staff name");
      return;
    }
    const newStaff = {
      id: staffList.length > 0 ? Math.max(...staffList.map(s => s.id)) + 1 : 1,
      name: newStaffName,
      position: newStaffPosition,
      theater: newStaffTheater,
      efficiency: 85
    };
    setStaffList([...staffList, newStaff]);
    setNewStaffName('');
    alert('Staff member added successfully!');
  };

  const handleTheaterStatusChange = (theater, status) => {
    setTheaterStatus({
      ...theaterStatus,
      [theater]: status
    });
  };

  const handleGuestCountChange = (amount) => {
    setGuestCount(Math.max(0, guestCount + amount));
  };

  const handleCustomGuestCountChange = (newCount) => {
    const count = parseInt(newCount);
    if (!isNaN(count) && count >= 0) {
      setGuestCount(count);
    }
  };

  const handleLiveWaitTimeChange = (amount) => {
    setLiveWaitTime(Math.max(0, liveWaitTime + amount));
    setTimeout(updateHourlyWaitTimes, 10);
  };

  const handleCustomWaitTimeChange = (newTime) => {
    const time = parseInt(newTime);
    if (!isNaN(time) && time >= 0) {
      setLiveWaitTime(time);
      setTimeout(updateHourlyWaitTimes, 10);
    }
  };

  const updateHourlyWaitTimes = () => {
    const currentHour = new Date().getHours();
    let hourString = currentHour >= 12
      ? `${currentHour > 12 ? currentHour - 12 : currentHour} PM`
      : `${currentHour === 0 ? 12 : currentHour} AM`;

    setHourlyWaitTimes(hourlyWaitTimes.map(item =>
      item.hour === hourString
        ? { ...item, waitTime: liveWaitTime }
        : item
    ));
  };

  // --- Calculations ---
  const averageWaitTime = Math.round(
    hourlyWaitTimes.reduce((sum, item) => sum + item.waitTime, 0) / hourlyWaitTimes.length
  );
  const averageEfficiency = Math.round(
    hourlyEfficiency.reduce((sum, item) => sum + item.efficiency, 0) / hourlyEfficiency.length
  );

  // --- UI Styles ---
  const buttonStyle = {
    padding: "10px 18px",
    backgroundColor: "#0066b2",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontWeight: "600",
    fontSize: "16px",
    margin: "0 8px 0 0"
  };
  const secondaryButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#10b981"
  };
  const navButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#f1f5fb",
    color: "#0066b2",
    border: "1px solid #e0e8f5"
  };
  const statusThStyle = {
    padding: '10px',
    border: '1px solid #e0e8f5',
    backgroundColor: '#f1f5fb',
    fontWeight: '600',
    color: '#0066b2'
  };
  const statusTdStyle = {
    padding: '10px',
    border: '1px solid #e0e8f5',
    textAlign: 'center'
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Operational': return '#10b981';
      case 'Maintenance': return '#f59e0b';
      case 'Down': return '#ef4444';
      default: return '#10b981';
    }
  };

  // --- Main Render ---
  return (
    <div
      style={{
        padding: "32px",
        backgroundColor: "#f5f9ff",
        fontFamily: '"Avenir Next", "Segoe UI", Arial, sans-serif',
        borderRadius: "16px",
        boxShadow: "0 4px 20px rgba(0,0,0,0.08)",
        maxWidth: "1100px",
        margin: "40px auto",
        border: "1px solid #e0e8f5"
      }}
    >
      {/* Header */}
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          marginBottom: "24px",
          borderBottom: "2px solid #e0e8f5",
          paddingBottom: "18px"
        }}
      >
        <div style={{ display: "flex", alignItems: "center" }}>
          <div
            style={{
              width: "48px",
              height: "48px",
              backgroundColor: "#0066b2",
              borderRadius: "10px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              color: "white",
              fontWeight: "bold",
              fontSize: "24px",
              marginRight: "16px"
            }}
          >
            <span role="img" aria-label="castle">
              
            </span>
          </div>
          <div>
            <h1
              style={{
                color: "#0066b2",
                fontSize: "28px",
                margin: "0 0 4px 0",
                fontWeight: "700",
                letterSpacing: "-0.5px"
              }}
            >
              SoarOps Command Center
            </h1>
            <div style={{
              display: 'flex',
              alignItems: 'center',
              color: '#637385',
              fontSize: '15px',
              gap: '20px'
            }}>
              <p style={{ margin: 0 }}>Attraction Operations Management</p>
            </div>
          </div>
        </div>
        <div>
          <button
            style={navButtonStyle}
            onClick={() => {
              setShowCastAccess(false);
              setIsAdminMode(false);
            }}
          >
            Home
          </button>
          <button
            style={navButtonStyle}
            onClick={() => {
              setShowCastAccess(false);
              setShowAdminModal(true);
            }}
          >
            Leadership Access
          </button>
          <button
            style={secondaryButtonStyle}
            onClick={() => {
              setShowCastAccess(true);
              setIsAdminMode(false);
              setShowAdminModal(false);
            }}
          >
            Cast Access
          </button>
        </div>
      </div>

      {/* Admin Modal */}
      {showAdminModal && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000
          }}
        >
          <div
            style={{
              backgroundColor: "white",
              padding: "32px",
              borderRadius: "12px",
              width: "350px",
              boxShadow: "0 4px 20px rgba(0,0,0,0.15)"
            }}
          >
            <h2
              style={{
                margin: "0 0 20px 0",
                color: "#0066b2",
                fontSize: "22px"
              }}
            >
              Leadership Authentication
            </h2>
            <p
              style={{
                fontSize: "14px",
                color: "#637385",
                marginBottom: "15px"
              }}
            >
              Enter leadership code to access admin features:
            </p>
            <input
              type="password"
              value={adminCode}
              onChange={(e) => setAdminCode(e.target.value)}
              placeholder="Enter code"
              style={{
                width: "100%",
                padding: "12px",
                borderRadius: "6px",
                border: "1px solid #ddd",
                marginBottom: "20px",
                fontSize: "15px"
              }}
              onKeyDown={(e) => {
                if (e.key === "Enter") {
                  verifyAdminCode();
                }
              }}
            />
            <div style={{ display: "flex", justifyContent: "flex-end", gap: "10px" }}>
              <button
                style={navButtonStyle}
                onClick={() => setShowAdminModal(false)}
              >
                Cancel
              </button>
              <button style={buttonStyle} onClick={verifyAdminCode}>
                Login
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Cast Access Page */}
      {showCastAccess && (
        <div
          style={{
            background: "white",
            borderRadius: "12px",
            padding: "36px",
            boxShadow: "0 2px 10px rgba(0,0,0,0.06)",
            border: "1px solid #eef2f8",
            maxWidth: "420px",
            margin: "0 auto"
          }}
        >
          <h2 style={{ color: "#0066b2", fontWeight: 700, marginBottom: 12 }}>
            Cast Member Portal
          </h2>
          <div style={{ marginBottom: 22 }}>
            <div style={{ fontSize: 18, marginBottom: 4 }}>
              <b>Status:</b>{" "}
              <span
                style={{
                  color:
                    castStatus === "On Duty"
                      ? "#10b981"
                      : castStatus === "On Break"
                      ? "#f59e0b"
                      : "#637385"
                }}
              >
                {castStatus}
              </span>
            </div>
            <div style={{ fontSize: 16, marginBottom: 8 }}>
              <b>Assignment:</b>{" "}
              {castAssignment ? (
                <span>
                  {castAssignment.position} - {theaterNames[castAssignment.theater]}
                </span>
              ) : (
                <span style={{ color: "#999" }}>None</span>
              )}
            </div>
          </div>
          <div style={{ display: "flex", flexDirection: "column", gap: 12 }}>
            <button
              style={buttonStyle}
              onClick={handleClockIn}
              disabled={castStatus === "On Duty"}
            >
              Clock-In
            </button>
            <button
              style={buttonStyle}
              onClick={handleGetAssignment}
              disabled={castStatus !== "On Duty"}
            >
              Get Assignment
            </button>
            <button
              style={buttonStyle}
              onClick={handleLunch}
              disabled={castStatus !== "On Duty"}
            >
              Lunch
            </button>
            <button
              style={buttonStyle}
              onClick={handleClockOut}
              disabled={castStatus === "Off Duty"}
            >
              Clock Out
            </button>
          </div>
        </div>
      )}

      {/* Leadership/Admin Mode */}
      {isAdminMode && !showCastAccess && (
        <div>
          {/* Staff Management Button */}
          <div style={{
            backgroundColor: 'white',
            padding: '15px',
            borderRadius: '10px',
            boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
            border: '1px solid #eef2f8',
            marginBottom: '25px',
            display: 'flex',
            gap: '15px'
          }}>
            <button
              style={{
                padding: '10px 15px',
                backgroundColor: showStaffPanel ? '#dcfce7' : '#10b981',
                color: showStaffPanel ? '#10b981' : 'white',
                border: showStaffPanel ? '1px solid #10b981' : 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontWeight: '500',
                fontSize: '14px'
              }}
              onClick={() => setShowStaffPanel(!showStaffPanel)}
            >
              {showStaffPanel ? 'Close Staff Management' : 'Staff Management'}
            </button>
            <button
              style={buttonStyle}
              onClick={() => setIsAdminMode(false)}
            >
              Exit Leadership Mode
            </button>
          </div>

          {/* Staff Management Panel */}
          {showStaffPanel && (
            <div style={{
              backgroundColor: 'white',
              padding: '25px',
              borderRadius: '10px',
              boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
              border: '1px solid #eef2f8',
              marginBottom: '25px'
            }}>
              <h3 style={{
                color: '#0066b2',
                margin: '0 0 20px 0',
                fontSize: '18px',
                fontWeight: '600'
              }}>
                Staff Management
              </h3>
              {/* Add Staff Form */}
              <div style={{
                backgroundColor: '#f8fbff',
                padding: '20px',
                borderRadius: '8px',
                border: '1px solid #eef2f8',
                marginBottom: '20px'
              }}>
                <h4 style={{
                  margin: '0 0 15px 0',
                  color: '#0066b2',
                  fontSize: '16px',
                  fontWeight: '600'
                }}>Add New Staff Member</h4>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', marginBottom: '20px' }}>
                  <div>
                    <label style={{
                      display: 'block',
                      marginBottom: '5px',
                      fontSize: '14px',
                      color: '#637385'
                    }}>
                      Full Name:
                    </label>
                    <input
                      type="text"
                      value={newStaffName}
                      onChange={(e) => setNewStaffName(e.target.value)}
                      placeholder="Enter staff name"
                      style={{
                        width: '100%',
                        padding: '10px',
                        borderRadius: '4px',
                        border: '1px solid #ddd',
                        fontSize: '14px'
                      }}
                    />
                  </div>
                  <div>
                    <label style={{
                      display: 'block',
                      marginBottom: '5px',
                      fontSize: '14px',
                      color: '#637385'
                    }}>
                      Position:
                    </label>
                    <select
                      value={newStaffPosition}
                      onChange={(e) => setNewStaffPosition(e.target.value)}
                      style={{
                        width: '100%',
                        padding: '10px',
                        borderRadius: '4px',
                        border: '1px solid #ddd',
                        fontSize: '14px'
                      }}
                    >
                      {positions.map((position, index) => (
                        <option key={index} value={position}>{position}</option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label style={{
                      display: 'block',
                      marginBottom: '5px',
                      fontSize: '14px',
                      color: '#637385'
                    }}>
                      Theater:
                    </label>
                    <select
                      value={newStaffTheater}
                      onChange={(e) => setNewStaffTheater(e.target.value)}
                      style={{
                        width: '100%',
                        padding: '10px',
                        borderRadius: '4px',
                        border: '1px solid #ddd',
                        fontSize: '14px'
                      }}
                    >
                      <option value="A">A</option>
                      <option value="B">B</option>
                      <option value="C">C</option>
                    </select>
                  </div>
                </div>
                <button
                  style={{
                    padding: '10px 20px',
                    backgroundColor: '#10b981',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontWeight: '500',
                    fontSize: '15px'
                  }}
                  onClick={handleAddStaff}
                >
                  Add Staff
                </button>
              </div>
              {/* Staff Table */}
              <table style={{
                width: '100%',
                borderCollapse: 'collapse',
                backgroundColor: '#fff'
              }}>
                <thead>
                  <tr style={{ backgroundColor: '#f1f5fb' }}>
                    <th style={{ padding: '10px', border: '1px solid #e0e8f5' }}>Name</th>
                    <th style={{ padding: '10px', border: '1px solid #e0e8f5' }}>Position</th>
                    <th style={{ padding: '10px', border: '1px solid #e0e8f5' }}>Theater</th>
                    <th style={{ padding: '10px', border: '1px solid #e0e8f5' }}>Efficiency</th>
                  </tr>
                </thead>
                <tbody>
                  {staffList.map(staff => (
                    <tr key={staff.id}>
                      <td style={{ padding: '10px', border: '1px solid #e0e8f5' }}>{staff.name}</td>
                      <td style={{ padding: '10px', border: '1px solid #e0e8f5' }}>
                        <select
                          value={staff.position}
                          onChange={e => handlePositionChange(staff.id, e.target.value)}
                          style={{
                            padding: '5px',
                            borderRadius: '4px',
                            border: '1px solid #ddd',
                            fontSize: '13px'
                          }}
                        >
                          {positions.map((position, idx) => (
                            <option key={idx} value={position}>{position}</option>
                          ))}
                        </select>
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #e0e8f5' }}>
                        <select
                          value={staff.theater}
                          onChange={e => handleTheaterChange(staff.id, e.target.value)}
                          style={{
                            padding: '5px',
                            borderRadius: '4px',
                            border: '1px solid #ddd',
                            fontSize: '13px'
                          }}
                        >
                          <option value="A">A</option>
                          <option value="B">B</option>
                          <option value="C">C</option>
                        </select>
                      </td>
                      <td style={{ padding: '10px', border: '1px solid #e0e8f5' }}>{staff.efficiency}%</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Main Admin Dashboard */}
          <div style={{
            display: 'flex',
            gap: '30px',
            flexWrap: 'wrap'
          }}>
            {/* Left Column */}
            <div style={{ flex: 1, minWidth: '320px' }}>
              {/* Guest Count */}
              <div style={{
                backgroundColor: 'white',
                borderRadius: '10px',
                padding: '24px',
                marginBottom: '20px',
                boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
                border: '1px solid #eef2f8'
              }}>
                <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 10px 0' }}>Current Guest Count</h3>
                <div style={{ fontSize: '32px', fontWeight: 'bold', color: '#10b981', marginBottom: '10px' }}>
                  {guestCount.toLocaleString()}
                </div>
                <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
                  <button onClick={() => handleGuestCountChange(100)} style={buttonStyle}>+100</button>
                  <button onClick={() => handleGuestCountChange(-100)} style={buttonStyle}>-100</button>
                  <button onClick={() => handleGuestCountChange(1000)} style={buttonStyle}>+1000</button>
                  <button onClick={() => handleGuestCountChange(-1000)} style={buttonStyle}>-1000</button>
                </div>
                <input
                  type="number"
                  min="0"
                  value={guestCount}
                  onChange={e => handleCustomGuestCountChange(e.target.value)}
                  style={{
                    width: '100%',
                    padding: '8px',
                    borderRadius: '5px',
                    border: '1px solid #ddd',
                    fontSize: '15px'
                  }}
                />
              </div>

              {/* Theater Status */}
              <div style={{
                backgroundColor: 'white',
                borderRadius: '10px',
                padding: '24px',
                boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
                border: '1px solid #eef2f8'
              }}>
                <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 10px 0' }}>Theater Status</h3>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead>
                    <tr>
                      <th style={statusThStyle}>Theater</th>
                      <th style={statusThStyle}>Status</th>
                      <th style={statusThStyle}>Change</th>
                    </tr>
                  </thead>
                  <tbody>
                    {['A', 'B', 'C'].map(theater => (
                      <tr key={theater}>
                        <td style={statusTdStyle}>{theater}</td>
                        <td style={{
                          ...statusTdStyle,
                          color: getStatusColor(theaterStatus[theater]),
                          fontWeight: 'bold'
                        }}>
                          {theaterStatus[theater]}
                        </td>
                        <td style={statusTdStyle}>
                          <select
                            value={theaterStatus[theater]}
                            onChange={e => handleTheaterStatusChange(theater, e.target.value)}
                            style={{
                              padding: '5px',
                              borderRadius: '4px',
                              border: '1px solid #ddd',
                              fontSize: '13px'
                            }}
                          >
                            <option value="Operational">Operational</option>
                            <option value="Maintenance">Maintenance</option>
                            <option value="Down">Down</option>
                          </select>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            {/* Right Column */}
            <div style={{ flex: 2, minWidth: '340px' }}>
              {/* Live Wait Time */}
              <div style={{
                backgroundColor: 'white',
                borderRadius: '10px',
                padding: '24px',
                marginBottom: '20px',
                boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
                border: '1px solid #eef2f8'
              }}>
                <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 10px 0' }}>Live Wait Time</h3>
                <div style={{ fontSize: '32px', fontWeight: 'bold', color: '#f59e0b', marginBottom: '10px' }}>
                  {liveWaitTime} min
                </div>
                <div style={{ display: 'flex', gap: '10px', marginBottom: '10px' }}>
                  <button onClick={() => handleLiveWaitTimeChange(1)} style={buttonStyle}>+1</button>
                  <button onClick={() => handleLiveWaitTimeChange(-1)} style={buttonStyle}>-1</button>
                  <button onClick={() => handleLiveWaitTimeChange(5)} style={buttonStyle}>+5</button>
                  <button onClick={() => handleLiveWaitTimeChange(-5)} style={buttonStyle}>-5</button>
                </div>
                <input
                  type="number"
                  min="0"
                  value={liveWaitTime}
                  onChange={e => handleCustomWaitTimeChange(e.target.value)}
                  style={{
                    width: '100%',
                    padding: '8px',
                    borderRadius: '5px',
                    border: '1px solid #ddd',
                    fontSize: '15px'
                  }}
                />
              </div>

              {/* Wait Time History */}
              <div style={{
                backgroundColor: 'white',
                borderRadius: '10px',
                padding: '24px',
                marginBottom: '20px',
                boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
                border: '1px solid #eef2f8'
              }}>
                <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 10px 0' }}>Hourly Wait Times</h3>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead>
                    <tr>
                      <th style={statusThStyle}>Hour</th>
                      <th style={statusThStyle}>Wait Time (min)</th>
                    </tr>
                  </thead>
                  <tbody>
                    {hourlyWaitTimes.map((item, idx) => (
                      <tr key={idx}>
                        <td style={statusTdStyle}>{item.hour}</td>
                        <td style={statusTdStyle}>{item.waitTime}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <div style={{ marginTop: '10px', color: '#637385', fontSize: '15px' }}>
                  <b>Average Wait Time:</b> {averageWaitTime} min
                </div>
              </div>

              {/* Efficiency History */}
              <div style={{
                backgroundColor: 'white',
                borderRadius: '10px',
                padding: '24px',
                boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
                border: '1px solid #eef2f8'
              }}>
                <h3 style={{ color: '#0066b2', fontSize: '18px', margin: '0 0 10px 0' }}>Hourly Efficiency</h3>
                <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                  <thead>
                    <tr>
                      <th style={statusThStyle}>Hour</th>
                      <th style={statusThStyle}>Efficiency (%)</th>
                    </tr>
                  </thead>
                  <tbody>
                    {hourlyEfficiency.map((item, idx) => (
                      <tr key={idx}>
                        <td style={statusTdStyle}>{item.hour}</td>
                        <td style={statusTdStyle}>{item.efficiency}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <div style={{ marginTop: '10px', color: '#637385', fontSize: '15px' }}>
                  <b>Average Efficiency:</b> {averageEfficiency}%
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Main Dashboard (Home) */}
      {!showCastAccess && !isAdminMode && (
        <div style={{ marginTop: 32 }}>
          <div
            style={{
              display: "flex",
              gap: "32px",
              flexWrap: "wrap",
              justifyContent: "space-between"
            }}
          >
            <div
              style={{
                flex: 1,
                minWidth: "260px",
                background: "white",
                borderRadius: "10px",
                padding: "28px",
                boxShadow: "0 2px 10px rgba(0,0,0,0.05)",
                border: "1px solid #eef2f8"
              }}
            >
              <h3 style={{ color: "#0066b2", fontSize: "19px", margin: "0 0 10px 0" }}>
                Current Guest Count
              </h3>
              <div
                style={{
                  fontSize: "34px",
                  fontWeight: "bold",
                  color: "#10b981",
                  marginBottom: "10px"
                }}
              >
                {guestCount.toLocaleString()}
              </div>
            </div>
            <div
              style={{
                flex: 1,
                minWidth: "260px",
                background: "white",
                borderRadius: "10px",
                padding: "28px",
                boxShadow: "0 2px 10px rgba(0,0,0,0.05)",
                border: "1px solid #eef2f8"
              }}
            >
              <h3 style={{ color: "#0066b2", fontSize: "19px", margin: "0 0 10px 0" }}>
                Live Wait Time
              </h3>
              <div
                style={{
                  fontSize: "34px",
                  fontWeight: "bold",
                  color: "#f59e0b",
                  marginBottom: "10px"
                }}
              >
                {liveWaitTime} min
              </div>
            </div>
          </div>
          <div style={{ marginTop: 32, color: "#637385", fontSize: "15px" }}>
            <b>Welcome to SoarOps!</b> For Cast features, use the <b>Cast Access</b> button above.
          </div>
        </div>
      )}
    </div>
  );
}

export default SoarOps;
