import React, { useState, useEffect } from "react";

// Helper for random assignment
function getRandom(arr) {
  return arr[Math.floor(Math.random() * arr.length)];
}

function SoarOps() {
  // --- State variables ---
  const [showAdminModal, setShowAdminModal] = useState(false);
  const [adminCode, setAdminCode] = useState("");
  const [isAdminMode, setIsAdminMode] = useState(false);
  const [showStaffPanel, setShowStaffPanel] = useState(false);
  const [showCastAccess, setShowCastAccess] = useState(false);

  // Cast Access State
  const [castStatus, setCastStatus] = useState("Off Duty");
  const [castAssignment, setCastAssignment] = useState(null);

  // For assignment
  const positions = [
    "Standby Greeter",
    "Lightning Lane 1",
    "Lightning Lane 2",
    "Tour Desk",
    "Lost & Found",
    "Pre-Merge",
    "Merge",
    "Tower A",
    "Tower B",
    "Tower C",
    "Grouper",
    "A Gate",
    "B Gate",
    "C Gate",
    "Assist",
    "Wheelchair Assist"
  ];
  const theaters = ["A", "B", "C"];
  const theaterNames = { A: "A Theater", B: "B Theater", C: "C Theater" };

  // --- Dashboard State (simplified for brevity, but you can expand as needed) ---
  const [guestCount, setGuestCount] = useState(36542);
  const [liveWaitTime, setLiveWaitTime] = useState(45);

  // --- Admin Modal Logic ---
  const verifyAdminCode = () => {
    if (adminCode === "123456789") {
      setIsAdminMode(true);
      setShowAdminModal(false);
    } else {
      alert("Invalid authorization code. Please try again or contact your supervisor.");
    }
  };

  // --- Cast Access Logic ---
  const handleClockIn = () => {
    setCastStatus("On Duty");
    setCastAssignment(null);
  };
  const handleGetAssignment = () => {
    setCastAssignment({
      position: getRandom(positions),
      theater: getRandom(theaters)
    });
    setCastStatus("On Duty");
  };
  const handleLunch = () => {
    setCastStatus("On Break");
  };
  const handleClockOut = () => {
    setCastStatus("Off Duty");
    setCastAssignment(null);
  };

  // --- UI Styles ---
  const buttonStyle = {
    padding: "10px 18px",
    backgroundColor: "#0066b2",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontWeight: "600",
    fontSize: "16px",
    margin: "0 8px 0 0"
  };
  const secondaryButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#10b981"
  };
  const navButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#f1f5fb",
    color: "#0066b2",
    border: "1px solid #e0e8f5"
  };

  // --- Main Render ---
  return (
    <div
      style={{
        padding: "32px",
        backgroundColor: "#f5f9ff",
        fontFamily: '"Avenir Next", "Segoe UI", Arial, sans-serif',
        borderRadius: "16px",
        boxShadow: "0 4px 20px rgba(0,0,0,0.08)",
        maxWidth: "900px",
        margin: "40px auto",
        border: "1px solid #e0e8f5"
      }}
    >
      {/* Header */}
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          marginBottom: "24px",
          borderBottom: "2px solid #e0e8f5",
          paddingBottom: "18px"
        }}
      >
        <div style={{ display: "flex", alignItems: "center" }}>
          <div
            style={{
              width: "48px",
              height: "48px",
              backgroundColor: "#0066b2",
              borderRadius: "10px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              color: "white",
              fontWeight: "bold",
              fontSize: "24px",
              marginRight: "16px"
            }}
          >
            <span role="img" aria-label="castle">
              üè∞
            </span>
          </div>
          <div>
            <h1
              style={{
                color: "#0066b2",
                fontSize: "28px",
                margin: "0 0 4px 0",
                fontWeight: "700",
                letterSpacing: "-0.5px"
              }}
            >
              SoarOps Dashboard
            </h1>
            <p
              style={{
                color: "#637385",
                fontSize: "15px",
                margin: 0
              }}
            >
              Disney Attraction Operations Management
            </p>
          </div>
        </div>
        <div>
          <button
            style={navButtonStyle}
            onClick={() => {
              setShowCastAccess(false);
              setIsAdminMode(false);
            }}
          >
            Home
          </button>
          <button
            style={navButtonStyle}
            onClick={() => {
              setShowCastAccess(false);
              setShowAdminModal(true);
            }}
          >
            Leadership Access
          </button>
          <button
            style={secondaryButtonStyle}
            onClick={() => {
              setShowCastAccess(true);
              setIsAdminMode(false);
            }}
          >
            Cast Access
          </button>
        </div>
      </div>

      {/* Admin Modal */}
      {showAdminModal && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000
          }}
        >
          <div
            style={{
              backgroundColor: "white",
              padding: "32px",
              borderRadius: "12px",
              width: "350px",
              boxShadow: "0 4px 20px rgba(0,0,0,0.15)"
            }}
          >
            <h2
              style={{
                margin: "0 0 20px 0",
                color: "#0066b2",
                fontSize: "22px"
              }}
            >
              Leadership Access
            </h2>
            <p
              style={{
                fontSize: "14px",
                color: "#637385",
                marginBottom: "15px"
              }}
            >
              Enter leadership code to access admin features:
            </p>
            <input
              type="password"
              value={adminCode}
              onChange={(e) => setAdminCode(e.target.value)}
              placeholder="Enter code"
              style={{
                width: "100%",
                padding: "12px",
                borderRadius: "6px",
                border: "1px solid #ddd",
                marginBottom: "20px",
                fontSize: "15px"
              }}
              onKeyDown={(e) => {
                if (e.key === "Enter") {
                  verifyAdminCode();
                }
              }}
            />
            <div style={{ display: "flex", justifyContent: "flex-end", gap: "10px" }}>
              <button
                style={navButtonStyle}
                onClick={() => setShowAdminModal(false)}
              >
                Cancel
              </button>
              <button style={buttonStyle} onClick={verifyAdminCode}>
                Login
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Cast Access Page */}
      {showCastAccess && (
        <div
          style={{
            background: "white",
            borderRadius: "12px",
            padding: "36px",
            boxShadow: "0 2px 10px rgba(0,0,0,0.06)",
            border: "1px solid #eef2f8",
            maxWidth: "420px",
            margin: "0 auto"
          }}
        >
          <h2 style={{ color: "#0066b2", fontWeight: 700, marginBottom: 12 }}>
            Cast Member Portal
          </h2>
          <div style={{ marginBottom: 22 }}>
            <div style={{ fontSize: 18, marginBottom: 4 }}>
              <b>Status:</b>{" "}
              <span
                style={{
                  color:
                    castStatus === "On Duty"
                      ? "#10b981"
                      : castStatus === "On Break"
                      ? "#f59e0b"
                      : "#637385"
                }}
              >
                {castStatus}
              </span>
            </div>
            <div style={{ fontSize: 16, marginBottom: 8 }}>
              <b>Assignment:</b>{" "}
              {castAssignment ? (
                <span>
                  {castAssignment.position} - {theaterNames[castAssignment.theater]}
                </span>
              ) : (
                <span style={{ color: "#999" }}>None</span>
              )}
            </div>
          </div>
          <div style={{ display: "flex", flexDirection: "column", gap: 12 }}>
            <button
              style={buttonStyle}
              onClick={handleClockIn}
              disabled={castStatus === "On Duty"}
            >
              Clock-In
            </button>
            <button
              style={buttonStyle}
              onClick={handleGetAssignment}
              disabled={castStatus !== "On Duty"}
            >
              Get Assignment
            </button>
            <button
              style={buttonStyle}
              onClick={handleLunch}
              disabled={castStatus !== "On Duty"}
            >
              Lunch
            </button>
            <button
              style={buttonStyle}
              onClick={handleClockOut}
              disabled={castStatus === "Off Duty"}
            >
              Clock Out
            </button>
          </div>
        </div>
      )}

      {/* Main Dashboard */}
      {!showCastAccess && !isAdminMode && (
        <div style={{ marginTop: 32 }}>
          <div
            style={{
              display: "flex",
              gap: "32px",
              flexWrap: "wrap",
              justifyContent: "space-between"
            }}
          >
            <div
              style={{
                flex: 1,
                minWidth: "260px",
                background: "white",
                borderRadius: "10px",
                padding: "28px",
                boxShadow: "0 2px 10px rgba(0,0,0,0.05)",
                border: "1px solid #eef2f8"
              }}
            >
              <h3 style={{ color: "#0066b2", fontSize: "19px", margin: "0 0 10px 0" }}>
                Current Guest Count
              </h3>
              <div
                style={{
                  fontSize: "34px",
                  fontWeight: "bold",
                  color: "#10b981",
                  marginBottom: "10px"
                }}
              >
                {guestCount.toLocaleString()}
              </div>
            </div>
            <div
              style={{
                flex: 1,
                minWidth: "260px",
                background: "white",
                borderRadius: "10px",
                padding: "28px",
                boxShadow: "0 2px 10px rgba(0,0,0,0.05)",
                border: "1px solid #eef2f8"
              }}
            >
              <h3 style={{ color: "#0066b2", fontSize: "19px", margin: "0 0 10px 0" }}>
                Live Wait Time
              </h3>
              <div
                style={{
                  fontSize: "34px",
                  fontWeight: "bold",
                  color: "#f59e0b",
                  marginBottom: "10px"
                }}
              >
                {liveWaitTime} min
              </div>
            </div>
          </div>
          <div style={{ marginTop: 32, color: "#637385", fontSize: "15px" }}>
            <b>Welcome to SoarOps!</b> For Cast features, use the <b>Cast Access</b> button above.
          </div>
        </div>
      )}

      {/* Leadership/Admin Mode Placeholder */}
      {isAdminMode && !showCastAccess && (
        <div
          style={{
            margin: "40px auto",
            background: "white",
            borderRadius: "12px",
            padding: "36px",
            boxShadow: "0 2px 10px rgba(0,0,0,0.06)",
            border: "1px solid #eef2f8",
            textAlign: "center"
          }}
        >
          <h2 style={{ color: "#0066b2", fontWeight: 700, marginBottom: 18 }}>
            Leadership Dashboard
          </h2>
          <p style={{ color: "#637385", fontSize: "16px" }}>
            Full admin features would appear here.
          </p>
          <button
            style={buttonStyle}
            onClick={() => setIsAdminMode(false)}
          >
            Exit Leadership Mode
          </button>
        </div>
      )}
    </div>
  );
}

export default SoarOps;
